// Register
router.post("/register", async (req, res) => {
  const {
    email,
    password,
    name,
    surname,
    residence,
    nationality,
    dob,
    profession,
    homevia,
    homenumber,
    province,
    postalcode,
    phone,
    neocheckId,
    verified,
    img
  } = req.body;

  try {
    const exist = await User.findOne({ email });
    let success = false;
    if (exist) {
      res.json({ error: "An account with this email already exist!" });
    }

    const salt = await bcrypt.genSalt(10);
    let securePassword = await bcrypt.hash(password, salt);
    await User.create({
      email,
      password: securePassword,
      name,
      surname,
      residence,
      nationality,
      profession,
      homevia,
      homenumber,
      province,
      postalcode,
      phone,
      dob,
      neocheckId,
      verified,
      img
    });

    await transporter.sendMail({
      from: "themohdhasnain@gmail.com", // sender address
      to: email, // list of receivers
      subject: "Identity Verification", // Subject line
      html: `
      <img src='cid:data:image/png;base64,' 
      width="200px" style="margin-top: 10px"/>
      <p style="margin-top: 10px; font-size: 1rem">Verifique su identidad haciendo clic en el siguiente bot√≥n:</p>
     <button style="margin-top: 15px; background-color: blue; 
     border: none !important; outline: none; border-radius: 5px; padding: 5px 0px;">
     <a href="http://localhost:3000/verify?id=${neocheckId}&mail=${email}"
     target="_blank"
     style="color: white !important; text-decoration: none !important;
      padding: 10px; font-size: 1rem">Verificar ahora</a></button>`,
      attachments: [
        {
          filename: "logo.png",
          path: "data:image/png;base64,",
          cid: "data:image/png;base64,", //same cid value as in the html img src
        },
      ],
    });

    success = true;
    res.json({ success, msg: "Successfully registered!" });
  } catch (err) {
    console.log(err);
  }
});

router.post("/login", async (req, res) => {
  const { email, password } = req.body;

  const user = await User.findOne({ email });
  let success = false;
  if (!user) {
    return res.json({ msg: "Incorrect Credentails!" });
  }

  const passwordCompare = await bcrypt.compare(password, user.password);
  if (!passwordCompare) {
    return res.status(400).json({ success, msg: "Invalid credentials!" });
  }

  const data = {
    user: {
      email: user.email,
      id: user.id,
      isAdmin: user.isAdmin,
      neocheckId: user.neocheckId,
    },
  };

  const authToken = jwt.sign(data, process.env.JWT_SECRET);
  success = true;

  res.json({ success, msg: "Successfull!", authToken });
});

router.post("/user", verifyToken, async (req, res) => {
  const user = await User.findById(req.user.id).select("-password");
  res.json(user);
});

// Update
router.put("/updatestatus/:email", async (req, res) => {
  const user = await User.findOne({ email: req.params.email });

  if (!user) return res.status(400).send("No account on this mail!");

  user.verified = req.body.verified;
  await user.save();

  res.json({ ...user._doc });
});

router.put("/update/:id", verifyTokenAndAuthorization, async (req, res) => {
  const { newUser } = req.body;

  try {
    let user = await User.findById(req.params.id);
    user = await User.findByIdAndUpdate(
      req.params.id,
      { $set: newUser },
      { new: true }
    );
    res.json({ user });
  } catch (error) {
    console.error(error.message);
    res.status(500).send(error.message);
  }
});

